  Math.floor(new Date($json["body"]["13"].replace(
    /(\d{2})\.(\d{2})\.(\d{4})/, // Match the pattern "DD.MM.YYYY"
    "$2/$1/$3" // Rearrange to "MM/DD/YYYY" (needed for JavaScript Date parsing)
  )).getTime() / 1000) // Convert to milliseconds and divide by 1000 to get Unix timestamp (in seconds)
}}

