={{ 
  Array.from([
    // Extracts value from Typeform response (field 8), or returns null if not found
    $('New submission via Gravityforms')?.item?.json?.body?.['8'] || null, 
    $('New submission via Gravityforms').item.json.body['13'],
    (() => {
      try {
        // Extracts and parses JSON from Typeform response (field 11), or returns null if not found
        let value = $('New submission via Gravityforms')?.item?.json?.body?.['11'];
        return value ? JSON.parse(value) : null;
      } catch (error) {
        return null; // Return null if JSON parsing fails
      }
    })()
  ].flat().filter(item => item !== null)) // Flattens the array and removes null values
}}
