={{      
// Converts the date into an UNIX timestamp since this is the expected format for date values in KlickTipp.
new Date(new Date($json.payload.scheduled_event.start_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getTime() / 1000  }}

={{      
// Converts the date into an UNIX timestamp since this is the expected format for date values in KlickTipp.
new Date(new Date($json.payload.scheduled_event.end_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getTime() / 1000  }}

={{      
// Converts the time to seconds since midnight since this is the expected format for time values in KlickTipp.
new Date(new Date($json.payload.scheduled_event.start_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getHours() * 3600      + new Date(new Date($json.payload.scheduled_event.start_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getMinutes() * 60      + new Date(new Date($json.payload.scheduled_event.start_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getSeconds()  }}

={{  
// Converts the time to seconds since midnight since this is the expected format for time values in KlickTipp.
new Date(new Date($json.payload.scheduled_event.end_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getHours() * 3600      + new Date(new Date($json.payload.scheduled_event.end_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getMinutes() * 60      + new Date(new Date($json.payload.scheduled_event.end_time).toLocaleString('en-US', { timeZone: $json.payload.timezone })).getSeconds()  }}

={{ 
  //Extracts first_name. If not available, extracts from name by taking all but the last word(s).

  $json.payload.first_name // Use first_name directly if available
    ? $json.payload.first_name 
    : $json.payload.name 
      ? $json.payload.name.split(' ').slice(0, -1).join(' ') // Extract all words except the last as first names
      : '' // Default to empty string if both are missing
}}

={{ 
  //Extracts last_name. If not available, extracts from name by taking the last word(s).
  $json.payload.last_name // Use last_name directly if available
    ? $json.payload.last_name 
    : $json.payload.name 
      ? $json.payload.name.split(' ').slice(-1).join('') // Extract the last word(s) as the last name
      : '' // Default to empty string if both are missing
}}

={{ 
  // Converts the phone number by replacing '+' with '00' and removing all spaces for standardization.
  $('New Calendly event').item.json.payload.text_reminder_number
    .replace('+', '00') // Replace '+' with '00'
    .replace(/\s+/g, '') // Remove all spaces
}}

