// Define an array of objects representing node types that have multiple outputs.
// Each object specifies the node type and the number of outputs it has.
const MULTI_OUTPUT_NODES = [
  { type: 'n8n-nodes-base.compareDatasets', outputs: 4 }, 
  { type: 'n8n-nodes-base.switch', outputs: 4}, 
  { type: 'n8n-nodes-base.if', outputs: 2}
]

// Initialize an empty array to store the affected workflows.
const affectedWorkflows = [];

// Loop through each item in the $input array.
for (const item of $input.all()) {
  // Get the workflow data from the item.
  const workflowData = item.json;

  const nodes = workflowData.nodes;
  const connections = workflowData.connections;

  // Initialize an empty array to store the potentially affected connections.
  const potentiallyAffectedNodes = [];

  for (const connectionName of Object.keys(connections)) {
    const connection = connections[connectionName];
    // Match connection by its name to get the node data
    const connectionNode = nodes.find(node => node.name === connectionName);

    // Check if the connection node is a multi-output node.
    const matchedMultiOutputNode = MULTI_OUTPUT_NODES.find(n => n.type === connectionNode.type);
    if(matchedMultiOutputNode) {
      const connectedOutputs = connection.main.filter(c => c && c.length > 0);

      // Check if the connection has empty outputs.
      const hasEmptyOutputs = connectedOutputs.length <  matchedMultiOutputNode.outputs;

      // If there are no connected outputs, skip this connection, it couldn't been affected by the migration
      if(connectedOutputs.length === 0) continue;

      // If the connection has empty outputs, it might have been affected by the wrong connections migration
      // which filtered-out empty indexes
      if(hasEmptyOutputs) potentiallyAffectedNodes.push(connectionName);
    }
  }

  if(potentiallyAffectedNodes.length > 0) {
    affectedWorkflows.push(
      { 
        workflowId: workflowData.id, 
        workflowName: workflowData.name,
        active: workflowData.active, 
        potentiallyAffectedNodes
      }
    )
  }
}

return {workflows: affectedWorkflows};

