// Retrieve the CSV-like data from the 'data' field in the input
const csvData = items[0].json.data; // Ensure that 'data' is the correct field name

// Check if csvData exists and is not empty
if (!csvData) {
  console.log("Input data structure:", ); // Debugging output to inspect input structure
  throw new Error('No CSV data provided. Ensure the correct field reference is being used.');
}

if (typeof csvData !== 'string') {
  throw new Error('CSV data is not a string. Please check the input data format.');
}

// Preprocess the CSV data to handle missing values
const preprocessedCsvData = csvData.replace(/,,/g, ',"",');

// Split the CSV into rows
const rows = preprocessedCsvData.split(/\r?\n/); // Adjust to handle different line endings

// Define the expected number of columns based on CSV structure
const expectedNumberOfColumns = 22;

// Iterate over each data row starting from the second row (index 1) and map it to an object
const output = rows.slice(1).map((row, index) => {
  // Split the row into values, accounting for empty columns using regex
  const values = row.split(/,(?=(?:(?:[^"]*"){2})*[^"]*$)/).map(value => {
    // Remove surrounding quotes from each value if present and trim whitespace
    return value.startsWith('"') && value.endsWith('"') ? value.slice(1, -1).trim() : value.trim();
  });

  // Ensure that the number of values matches the expected number of columns
  while (values.length < expectedNumberOfColumns) {
    values.push(""); // Add empty strings for missing values
  }

  // Check if the number of values matches expected columns
  if (values.length !== expectedNumberOfColumns) {
    console.warn(`Row ${index + 1} doesn't have the expected number of columns. Skipping this entry.`);
    return null; // Skip this row if it doesn't match the expected columns
  }

  // Create an item object with a fixed structure
  const item = {
    id: values[0] || null,
    campaign_lead_map_id: values[1] || null,
    status: values[2] || null,
    category: values[3] || null,
    is_interested: values[4] === 'true', // Convert to boolean
    created_at: values[5] || null,
    first_name: values[6] || null,
    last_name: values[7] || null,
    email: values[8] || null,
    phone_number: values[9] || null,
    company_name: values[10] || null,
    website: values[11] || null,
    location: values[12] || null,
    custom_fields: values[13] || null,
    linkedin_profile: values[14] || null,
    company_url: values[15] || null,
    is_unsubscribed: values[16] === 'true', // Convert to boolean
    unsubscribed_client_id_map: values[17] || null,
    last_email_sequence_sent: values[18] || null,
    open_count: parseInt(values[19], 10) || 0, // Convert to number
    click_count: parseInt(values[20], 10) || 0, // Convert to number
    reply_count: parseInt(values[21], 10) || 0  // Convert to number
  };

  return { json: item };
}).filter(item => item !== null); // Remove null entries from output

// Return the structured output
return output;
