// New function to remove topics with only one article - ultra-simple approach
function removeSingleArticleTopics(html) {
  // First, see if there are any topics with exactly 1 article
  if (!html.includes('<strong>1</strong> מאמרים')) {
    console.log('No topics with 1 article found');
    return html;
  }

  // Find each line that contains the "נושא" comment
  // and check if it has exactly 1 article mentioned
  const lines = html.split('\n');
  const linesToRemove = [];

  // For each line containing "1 מאמרים", find the topic it belongs to
  for (let i = 0; i < lines.length; i++) {
    if (lines[i].includes('<strong>1</strong> מאמרים')) {
      console.log(`Found line ${i} with 1 article mention`);

      // Go back to find the start of this topic
      let startLine = -1;
      for (let j = i; j >= 0; j--) {
        if (lines[j].includes('<!-- נושא') || 
            lines[j].includes('<div style="margin-bottom: 15px; padding-bottom: 15px; border-bottom:')) {
          startLine = j;
          break;
        }
      }

      if (startLine === -1) {
        console.log(`Couldn't find start of topic for line ${i}`);
        continue;
      }

      // Go forward to find the end of this topic
      let endLine = -1;
      let divCount = 0;
      for (let j = startLine; j < lines.length; j++) {
        // Count opening divs
        const openMatches = lines[j].match(/<div/g);
        if (openMatches) {
          divCount += openMatches.length;
        }

        // Count closing divs
        const closeMatches = lines[j].match(/<\/div>/g);
        if (closeMatches) {
          divCount -= closeMatches.length;
        }

        // When divCount returns to 0, we've found the end
        if (divCount === 0 && j > startLine) {
          endLine = j;
          break;
        }
      }

      if (endLine === -1) {
        console.log(`Couldn't find end of topic for line ${i}`);
        continue;
      }

      // Now we have the start and end lines of the topic
      console.log(`Found topic from line ${startLine} to ${endLine}`);

      // Mark these lines for removal
      for (let j = startLine; j <= endLine; j++) {
        linesToRemove.push(j);
      }
    }
  }

  // Remove the marked lines
  const newLines = [];
  for (let i = 0; i < lines.length; i++) {
    if (!linesToRemove.includes(i)) {
      newLines.push(lines[i]);
    }
  }

  console.log(`Removed ${linesToRemove.length} lines in total`);
  return newLines.join('\n');
