// Function to identify sentiment type from text
function getSentimentType(text) {
  if (!text) return 'neutral';

  const lowerText = text.toLowerCase();

  // Negative keywords - check first because there are expressions with both "positive" and "negative" together
  if (lowerText.includes('שלילי') || lowerText.includes('negative') || 
      lowerText.includes('bearish') || lowerText.includes('ירידה') || 
      lowerText.includes('דובי') || lowerText.includes('מכירה') || 
      lowerText.includes('שלילי-חזק') || lowerText.includes('שלילי-חלש') ||
      lowerText.includes('שלילית')) {
    return 'negative';
  }

  // Positive keywords
  if (lowerText.includes('חיובי') || lowerText.includes('positive') || 
      lowerText.includes('bullish') || lowerText.includes('עלייה') || 
      lowerText.includes('שורי') || lowerText.includes('קנייה') || 
      lowerText.includes('חיובי-חזק') || lowerText.includes('חיובי-חלש') ||
      lowerText.includes('חיובית')) {
    return 'positive';
  }

  // Additional check for expressions containing only "strong" or "weak"
  if (lowerText.includes('חזק')) {
    // If no negative word, assume it's positive
    return 'positive';
  }

  // Default - neutral
  return 'neutral';
}

