// New function to remove undefined articles from HTML
function removeUndefinedArticles(html) {
  // Find all article blocks
  const articleBlocksRegex = /<tr>\s*<td style="padding-bottom: 16px;">\s*<table[^>]*>[\s\S]*?<\/table>\s*<\/td>\s*<\/tr>/g;
  const articleBlocks = Array.from(html.matchAll(articleBlocksRegex));

  // No articles found
  if (!articleBlocks || articleBlocks.length === 0) {
    console.log("No article blocks found");
    return html;
  }

  // Function to check if an article is fully undefined
  function isFullyUndefinedArticle(articleHtml) {
    // An article is considered fully undefined if:
    // 1. It has href="undefined"
    // 2. It has link text that is "undefined"
    // 3. It has "undefined | undefined" (source and date)
    return articleHtml.includes('href="undefined"') && 
           articleHtml.includes('>undefined</a>') &&
           articleHtml.includes('undefined | undefined');
  }

  // Identify blocks to remove
  const blocksToRemove = [];
  for (const match of articleBlocks) {
    const block = match[0];
    if (isFullyUndefinedArticle(block)) {
      console.log("Found undefined article, will remove");
      blocksToRemove.push(match);
    } else {
      console.log("Found valid article, keeping it");
    }
  }

  // If no blocks to remove, return original HTML
  if (blocksToRemove.length === 0) {
    console.log("No undefined articles found to remove");
    return html;
  }

  console.log(`Found ${blocksToRemove.length} undefined articles to remove`);

  // Create a new string by removing the matches from end to start (to avoid index shifting)
  let cleanedHtml = html;
  for (let i = blocksToRemove.length - 1; i >= 0; i--) {
    const match = blocksToRemove[i];
    cleanedHtml = cleanedHtml.slice(0, match.index) + cleanedHtml.slice(match.index + match[0].length);
  }

  return cleanedHtml;
}

