// 3. Update article colors
const articleBlocks = updatedHtml.match(/<tr>\s*<td style="padding-bottom: 16px;">\s*<table[^>]*>[\s\S]*?<\/table>\s*<\/td>\s*<\/tr>/g);
if (articleBlocks) {
  for (const block of articleBlocks) {
