// Function to generate yesterday's date in the required format
function getYesterdayDateFormat() {
  // Create a current date object
  const today = new Date();

  // Set the date to the previous day (yesterday)
  today.setDate(today.getDate() - 1);

  // Reset hours, minutes, seconds and milliseconds to 00:00:00.000
  today.setHours(0, 0, 0, 0);

  // Extract components
  const year = today.getFullYear();
  const month = String(today.getMonth() + 1).padStart(2, '0'); // Months in JS start from 0
  const day = String(today.getDate()).padStart(2, '0');
  const hours = String(today.getHours()).padStart(2, '0');
  const minutes = String(today.getMinutes()).padStart(2, '0');

  // Build the string in the required format
  return `${year}${month}${day}T${hours}${minutes}`;
}
// Calculate the date
const yesterdayDate = getYesterdayDateFormat();
// Return the result in the format required by n8n - array of objects
return [
  {
    json: {
      wanted_date: yesterdayDate
    }
  }
];

/**
 * Custom code for n8n Code node to analyze news data from Alpha Vantage
 * 
 * - The code receives API data from the previous node
 * - Analyzes sentiment, leading articles, and hot topics
 * - Returns a structured JSON object for further processing
 */

const stockSymbol = $('Set Variables').first().json.stockSymbol
const allNews = $input.first().json.feed
  ;
const today = new Date().toISOString().split('T')[0]; // Current date in YYYY-MM-DD format

// Filter articles relevant to the stock
const relevantArticles = allNews.filter(article => {
  return article.ticker_sentiment && article.ticker_sentiment.some(ticker => 
    ticker.ticker === stockSymbol
  );
});

// Sentiment analysis
let sentimentCounts = {
  "Bullish": 0,
  "Somewhat-Bullish": 0,
  "Neutral": 0,
  "Somewhat-Bearish": 0,
  "Bearish": 0
};

let totalSentimentScore = 0;
let totalRelevanceScore = 0;

relevantArticles.forEach(article => {
  const stockTicker = article.ticker_sentiment.find(ticker => ticker.ticker === stockSymbol);
  if (stockTicker) {
    sentimentCounts[stockTicker.ticker_sentiment_label]++;
    totalSentimentScore += parseFloat(stockTicker.ticker_sentiment_score) * parseFloat(stockTicker.relevance_score);
    totalRelevanceScore += parseFloat(stockTicker.relevance_score);
  }
});

const avgSentimentScore = totalRelevanceScore > 0 ? totalSentimentScore / totalRelevanceScore : 0;

// Determining overall sentiment
let overallSentiment;
if (avgSentimentScore >= 0.35) {
  overallSentiment = "חיובי מאוד";
} else if (avgSentimentScore >= 0.15) {
  overallSentiment = "חיובי";
} else if (avgSentimentScore > -0.15) {
  overallSentiment = "נייטרלי";
} else if (avgSentimentScore > -0.35) {
  overallSentiment = "שלילי";
} else {
  overallSentiment = "שלילי מאוד";
}

// Most influential articles
const topArticles = relevantArticles
  .map(article => {
    const stockTicker = article.ticker_sentiment.find(ticker => ticker.ticker === stockSymbol);
    return {
      title: article.title,
      url: article.url,
      source: article.source,
      date: formatDate(article.time_published),
      sentiment_label: stockTicker ? stockTicker.ticker_sentiment_label : "N/A",
      sentiment_score: stockTicker ? parseFloat(stockTicker.ticker_sentiment_score) : 0,
      relevance_score: stockTicker ? parseFloat(stockTicker.relevance_score) : 0,
      impact_score: stockTicker ? Math.abs(parseFloat(stockTicker.ticker_sentiment_score) * parseFloat(stockTicker.relevance_score)) : 0
    };
  })
  .sort((a, b) => b.impact_score - a.impact_score)
  .slice(0, 5);

// Analysis of main topics
const topicsMap = {};

relevantArticles.forEach(article => {
  if (article.topics) {
    article.topics.forEach(topic => {
      if (!topicsMap[topic.topic]) {
        topicsMap[topic.topic] = {
          count: 0,
          relevance: 0
        };
      }
      topicsMap[topic.topic].count++;
      topicsMap[topic.topic].relevance += parseFloat(topic.relevance_score);
    });
  }
});

const hotTopics = Object.entries(topicsMap)
  .map(([topic, data]) => ({
    topic,
    article_count: data.count,
    average_relevance: (data.relevance / data.count).toFixed(2)
  }))
  .sort((a, b) => b.article_count - a.article_count)
  .slice(0, 5);

// Creating result object
const analysisResult = {
  stock_symbol: stockSymbol,
  analysis_date: today,
  sentiment_analysis: {
    overall_sentiment: overallSentiment,
    sentiment_score: parseFloat(avgSentimentScore.toFixed(4)),
    sentiment_distribution: sentimentCounts
  },
  top_articles: topArticles.map(article => ({
    title: article.title,
    source: article.source,
    url: article.url,
    date: article.date,
    sentiment: article.sentiment_label,
    impact_score: article.impact_score.toFixed(4)
  })),
  hot_topics: hotTopics,
  recent_trends: {
    description: getTrendDescription(overallSentiment, hotTopics),
    market_outlook: getMarketOutlook(overallSentiment)
  }
};

// Helper functions
function formatDate(dateStr) {
  if (!dateStr) return "N/A";

  try {
    // Format: 20250418T152049 -> 2025-04-18
    const year = dateStr.substring(0, 4);
    const month = dateStr.substring(4, 6);
    const day = dateStr.substring(6, 8);
    return `${year}-${month}-${day}`;
  } catch (e) {
    return dateStr;
  }
}

function getTrendDescription(sentiment, topics) {
  let description = "";

  if (sentiment === "חיובי מאוד" || sentiment === "חיובי") {
    description = "מגמה חיובית כאשר משקיעים מתמקדים בעיקר ב";
  } else if (sentiment === "שלילי מאוד" || sentiment === "שלילי") {
    description = "מגמה שלילית כאשר החששות העיקריים מתמקדים ב";
  } else {
    description = "מגמה מעורבת עם התמקדות ב";
  }

  if (topics.length > 0) {
    const topThreeTopics = topics.slice(0, Math.min(3, topics.length));
    description += topThreeTopics.map(t => t.topic).join(", ");
  } else {
    description += "מגוון נושאים";
  }

  return description + ".";
}

function getMarketOutlook(sentiment) {
  if (sentiment === "חיובי מאוד") {
    return "תחזית שוק חיובית מאוד. הסנטימנט הכללי מצביע על אמון משקיעים גבוה ופוטנציאל לעלייה בטווח הקצר.";
  } else if (sentiment === "חיובי") {
    return "תחזית שוק חיובית. ישנן אינדיקציות לאופטימיות זהירה בקרב משקיעים.";
  } else if (sentiment === "נייטרלי") {
    return "תחזית שוק מעורבת. קיימים כוחות מאזנים של אופטימיות ופסימיות בשוק.";
  } else if (sentiment === "שלילי") {
    return "תחזית שוק שלילית. ישנן דאגות בקרב משקיעים שעשויות להשפיע על המניה בטווח הקצר.";
  } else {
    return "תחזית שוק שלילית מאוד. קיימת אווירת זהירות משמעותית ונטייה למכירות.";
  }
}

// Return the object for further flow in n8n
return {
  json: analysisResult
};

