// Get historical price data
const data = $input.item.json;

// Check if data exists
if (!data.values || data.values.length === 0) {
  return { json: { error: "No price data available", ticker: data.meta?.symbol } };
}

// Convert prices to numbers
const prices = data.values.map(v => parseFloat(v.close)).reverse();

// Function to calculate Fibonacci levels
function calculateFibonacciLevels() {
  // Find min and max prices
  const max = Math.max(...prices);
  const min = Math.min(...prices);
  const diff = max - min;

  return {
    level_0: min.toFixed(2),
    level_0_236: (min + diff * 0.236).toFixed(2),
    level_0_382: (min + diff * 0.382).toFixed(2),
    level_0_5: (min + diff * 0.5).toFixed(2),
    level_0_618: (min + diff * 0.618).toFixed(2),
    level_0_786: (min + diff * 0.786).toFixed(2),
    level_1: max.toFixed(2)
  };
}
// Function to identify support and resistance levels
function findSupportResistanceLevels() {
  // We need at least 30 data points
  if (prices.length < 30) {
    return { support: [], resistance: [] };
  }

  const supportLevels = [];
  const resistanceLevels = [];

  // Check each point (except edges) if it's a local minimum or maximum
  const lookback = 5; // how many points to check in each direction

  for (let i = lookback; i < prices.length - lookback; i++) {
    // Check for local minimum (support)
    let isMinimum = true;
    for (let j = i - lookback; j < i; j++) {
      if (prices[j] <= prices[i]) {
        isMinimum = false;
        break;
      }
    }

    for (let j = i + 1; j <= i + lookback; j++) {
      if (prices[j] <= prices[i]) {
        isMinimum = false;
        break;
      }
    }

    if (isMinimum) {
      supportLevels.push(prices[i]);
    }

    // Check for local maximum (resistance)
    let isMaximum = true;
    for (let j = i - lookback; j < i; j++) {
      if (prices[j] >= prices[i]) {
        isMaximum = false;
        break;
      }
    }

    for (let j = i + 1; j <= i + lookback; j++) {
      if (prices[j] >= prices[i]) {
        isMaximum = false;
        break;
      }
    }

    if (isMaximum) {
      resistanceLevels.push(prices[i]);
    }
  }

  // Sort and remove duplicates
  const uniqueSupports = [...new Set(supportLevels)];
  const uniqueResistances = [...new Set(resistanceLevels)];

  // Return only significant levels (up to 5 of each)
  return {
    support: uniqueSupports.sort((a, b) => b - a).slice(0, 5).map(p => p.toFixed(2)),
    resistance: uniqueResistances.sort((a, b) => a - b).slice(0, 5).map(p => p.toFixed(2))
  };
}

// Calculate levels
const fibonacciLevels = calculateFibonacciLevels();
const supportResistanceLevels = findSupportResistanceLevels();

// Return information with additional stock data
return {
  json: {
    ticker: data.meta.symbol,
    currentPrice: parseFloat(data.values[0].close).toFixed(2),
    fibonacci: fibonacciLevels,
    supportResistance: supportResistanceLevels,
    dataPoints: prices.length
  }
};

