// Loop over input items and add a new field
// called 'myNewField' to the JSON of each one
matched = []
unmatched = []  

for (const item of $input.first().json.rule_results) {
  if (item.matched) {
    matched.push(item)
  } else {
    unmatched.push(item)    
  }
}

return {
  json: {
    matched,
    unmatched
  }
}

