// Recursive function to flatten JSON into a single string
function flattenJson(obj, prefix = '') {
 let str = '';
 for (const key in obj) {
 if (typeof obj[key] === 'object' && obj[key] !== null) {
 str += flattenJson(obj[key], `${prefix}${key}.`);
 } else {
 str += `${prefix}${key}: ${obj[key]}\n`;
 }
 }
 return str;
}

// Get input data
const data = $input.all();

// Initialize a variable to store the final output
let output = '';

// Process each item
data.forEach(item => {
 output += flattenJson(item.json);
 output += '\n---\n'; // Separator between records
});

// Return the merged string as output
return [{ json: { data: output } }];

// Input JSON from the previous node
const input = $json.output;

// Split the input into sections based on double newlines
const sections = input.split('\n\n');

// Initialize the result array
const result = [];

// Process each section
sections.forEach((section) => {
 const trimmedSection = section.trim();

 // Handle headings marked with ** (bold)
 if (/^\*\*(.*?)\*\*$/.test(trimmedSection)) {
 result.push({ type: 'heading', content: trimmedSection.replace(/\*\*(.*?)\*\*/, '<b>$1</b>') });
 }
 // Handle bullet lists marked with *
 else if (trimmedSection.startsWith('*')) {
 const listItems = trimmedSection.split('\n').map((item) => item.trim().replace(/^\*\s/, ''));
 result.push({ type: 'list', items: listItems });
 }
 // Handle numbered lists
 else if (/^\d+\.\s/.test(trimmedSection)) {
 const numberedItems = trimmedSection.split('\n').map((item) => item.trim().replace(/^\d+\.\s/, ''));
 result.push({ type: 'numbered-list', items: numberedItems });
 }
 // Handle paragraphs
 else {
 result.push({ type: 'paragraph', content: trimmedSection });
 }
});

// Return the result array
return result.map(item => ({ json: item }));

// Get input data from n8n
const inputData = $input.all(); // Fetch all input data items

// Function to convert JSON data into a single HTML string
function convertToHTML(data) {
 let html = '';

 data.forEach((item) => {
 switch (item.json.type) {
 case 'paragraph':
 html += `<p>${item.json.content}</p>`;
 break;
 case 'heading':
 html += `<h2>${item.json.content}</h2>`;
 break;
 case 'list':
 html += '<ul>';
 item.json.items.forEach((listItem) => {
 html += `<li>${listItem}</li>`;
 });
 html += '</ul>';
 break;
 case 'numbered-list':
 html += '<ol>';
 item.json.items.forEach((listItem) => {
 html += `<li>${listItem}</li>`;
 });
 html += '</ol>';
 break;
 default:
 break;
 }
 });

 return html;
}

// Convert inputData to a single HTML string
const singleHTML = convertToHTML(inputData);

// Return as a single item
return [{ json: { html: singleHTML } }];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
 item.json.myNewField = 1;
}

return $input.all();
