// Code here will run only once, no matter how many input items there are.
// More info and help: https://docs.n8n.io/nodes/n8n-nodes-base.function

// Loop over inputs and add a new field called 'myNewField' to the JSON of each one
for (item of items) {
  if (item.json.textHtml) {
    // Remove HTML, double quotations, line breaks, carriage returns
    item.json.body = item.json.textHtml.replace(/<br(\s*?\/?)>/g, "\\n").replace(/(<([^>]+)>)/g, "").replace(/\"/g, "");
    //item.json.body = item.json.textHtml.eplace(/(<([^>]+)>)/g, "").replace(/\"/g, "").replace(/\n/g, "").replace(/\r/g, "");
  } else {
    // Remove double quotations, line breaks, carriage returns
    item.json.body = item.json.textPlain.replace(/\"/g, "").replace(/\n/g, "\\n").replace(/\r/g, "");
  }
}

// You can write logs to the browser console
console.log('Done!');

return items;
