// created report header and custom table style
var md_reporthead="#Timesheet report\n";
var md_style =  (`
<style> table {border: 0.5px solid; border-spacing: 0px;}
        table th {border-bottom: 0.5px solid;}
        table thead {background: #D0E4F5;}
        table tr:nth-child(even) { background: #D8D8D8;}
</style>\n\n`);

var md_reportbody=md_style+md_reporthead;

//declare several variables that are used for report generation
var tablehead = "| Date | Hours | Task Description |\n|:---|:---:|---|\n";

var cur_user="";
var cur_usernum=0;

var cur_task="";
var cur_tasktotal=0;


for (item of items) {

  // Check if new user
  if (item.json.UserName != cur_user) {
    // Close previous user's task
    md_reportbody += (cur_tasktotal) ? "\n*"+cur_tasktotal.toFixed(2)+" - Total hours for this task*\n" : "";
    cur_tasktotal = 0; cur_task="";

    // add new user and embed avatar as base64 image
    cur_user = item.json.UserName;
    md_reportbody += `\n##![img](data:image/png;base64,${items[cur_usernum].binary.data.data}) ${cur_user}\n`;
    cur_usernum   += 1;
  } // Check for new user - ENDIF


  // Check if new task
  if (item.json.TaskTitle != cur_task) {

    // if not empty task - add total amount of hours for *previous* task
    md_reportbody += (cur_tasktotal) ? `\n*${cur_tasktotal.toFixed(2)} - Total hours for this task*\n` : "";

    // Add new task header and reset total hours counter
    cur_task = item.json.TaskTitle;
    md_reportbody += `\n###${cur_task}\n${tablehead}`;
    cur_tasktotal = 0;
  } // Check for new task - ENDIF

  // Add current task + update total hours
   md_reportbody += `| ${item.json.date.split('T',1)} | ${item.json.hours.toFixed(2)} | ${item.json.note} |\n`;
   cur_tasktotal += item.json.hours;
}

// Let's not forget the last task's total hours:
md_reportbody += (cur_tasktotal) ? `\n*${cur_tasktotal.toFixed(2)} - Total hours for this task*\n` : "";

// Finalise the report
md_reportbody += `\n*Timesheet report generated on: ${$now.toISODate()}*`;
md_reporthead += "\n";

return [{mdreport: md_reportbody}];
