parameters.text:
=start: {{ $json.start.dateTime }}
meeting url: {{ $json.hangoutLink }}
summary: {{ $json.summary }}
description: {{ $json.description }}
organiser: {{ $json.organizer.displayName }} ({{ $json.organizer.email }})
attendees: {{ $json.attendees.filter(item => !item.organizer).map(item => item.email).join(',') }}

parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"attendees": {
          "type": "array",
          "description": "list of attendees excluding the meeting organiser",
          "items": {
			"type": "object",
			"properties": {
			  "name": { "type": "string" },
              "email": { "type": "string" },
              "linkedin_url": { "type": "string" }
			}
          }
		}
	}
}

parameters.text:
=meeting date: {{ $('Check For Upcoming Meetings').item.json.start.dateTime }}
meeting url: {{ $('Check For Upcoming Meetings').item.json.hangoutLink }}
meeting summary: {{ $('Check For Upcoming Meetings').first().json.summary }}
meeting description: {{ $('Check For Upcoming Meetings').item.json.description }}
meeting with: {{ $json.attendees.map(item => item.name).join(',') }}
---
{{
$json.attendees.map(item => {
  return
`attendee name: ${item.name}
${item.name}'s last correspondance: ${item.email_summary.replaceAll('\n', ' ') || `We have not had any correspondance with ${item.name}`}
${item.name}'s linkedin profile: ${item.linkedin_summary.replaceAll('\n', ' ') || `We were unable to find the linkedin profile for ${$json.name}`}
`
}).join('\n---\n')
}}

=### name
{{ $('Extract Profile Metadata').item.json.name }}
### about
"{{ $('Extract Profile Metadata').item.json.tagline }}"
{{ $json.about.replaceAll('\n', ' ')}}
### recent activity
{{
$json.activity.map((item, idx) => {
  return [
    item.header.replace('View full post.', ''),
    `(${item.url})`,
    ' - ',
    item.content.replaceAll('\n', ' ').replaceAll('…show more', '')
  ].join(' ')
}).join('\n---\n')
}}
