const baseUrl = 'https://api.apollo.io/api/v1/mixed_people/search';
const seniorities = ['owner', 'founder', 'c_suite', 'partner', 'vp', 'head', 'director'];
const seniorityParams = seniorities.map(s => `person_seniorities[]=${encodeURIComponent(s)}`).join('&');
const domains = $input.all().map(item => item.json.Domain);
const domainParams = domains.map(d => `q_organization_domains_list[]=${encodeURIComponent(d)}`).join('&');
const perPageParam = 'per_page=10';
const fullUrl = `${baseUrl}?${seniorityParams}&${domainParams}&${perPageParam}`;
return [{ json: { url: fullUrl } }];

const details = $input.all().map(item => ({
  linkedin_url: item.json['LinkedIn Profile URL'],
  domain: item.json['Company Domain']
}));

return [{ json: { details } }];

// Extract all items from the input
const items = $input.all();

// Initialize Sets to store unique company domains and email addresses
const uniqueCompanies = new Set();
const uniqueEmails = new Set();

// Iterate through each item to populate the Sets
for (const item of items) {
  const domain = item.json['Company Domain'];
  const email = item.json['Email Address'];

  if (domain) uniqueCompanies.add(domain);
  if (email) uniqueEmails.add(email);
}

// Create the summary message
const message = `This week we discovered ${uniqueEmails.size} decision makers from ${uniqueCompanies.size} companies.`;

// Return the message as output
return [
  {
    json: {
      message,
      companies: uniqueCompanies.size,
      contacts: uniqueEmails.size,
    },
  },
];
