const data = $input.first().json.data || [];

function getText(post, reshared = false) {
  if (!post) return "";
  return reshared ? (post.resharedPost?.text || "") : (post.text || "");
}

function getDate(post) {
  if (!post) return "";
  return post.postedDate || post.postedDateTimestamp || "";
}

return [{
  json: {
    post_1: getText(data[0]),
    post_1_date: getDate(data[0]),

    post_2: getText(data[1]),
    post_2_date: getDate(data[1]),

    post_3: getText(data[2]),
    post_3_date: getDate(data[2]),

    post_4: getText(data[3], true),
    post_4_date: getDate(data[3]),

    post_5: getText(data[4], true),
    post_5_date: getDate(data[4]),
  }
}];

