// Safe LinkedIn profile details scraper for n8n
const profileData = {};
const input = $input.first() && $input.first().json ? $input.first().json : {};

// Basic profile information with fallbacks
if (input.about) profileData.summary = input.about;
if (input.headline) profileData.headline = input.headline;
if (input.addressCountryOnly) profileData.nationality = input.addressCountryOnly;

// Education information with fallbacks
if (input.educations && Array.isArray(input.educations) && input.educations.length > 0) {
  const education = input.educations[0];
  if (education.title) profileData.education = education.title;
  if (education.subtitle) profileData.fieldOfStudy = education.subtitle;
}

// Employment information with fallbacks
if (input.experiences && Array.isArray(input.experiences) && input.experiences.length > 0) {
  const experience = input.experiences[0];
  if (experience.subtitle) profileData.employment_company = experience.subtitle;
  if (experience.title) profileData.position = experience.title;
  if (experience.metadata) profileData.company_location = experience.metadata;
}

// Return the profile data in n8n expected format
return [{ json: profileData }];

