// n8n function to extract LinkedIn posts
const items = [];

// Check if input exists and has updates
if (!$input.first() || !$input.first().json || !$input.first().json.updates || !Array.isArray($input.first().json.updates)) {
  // Return a single item with "no posts" message
  items.push({
    json: {
      message: "No posts found"
    }
  });
  return items;
}

const updates = $input.first().json.updates;

// If no posts available, return the "no posts" message
if (updates.length === 0) {
  items.push({
    json: {
      message: "No posts found"
    }
  });
  return items;
}

// Process available posts (up to 4)
for (let i = 0; i < Math.min(updates.length, 4); i++) {
  if (updates[i] && updates[i].postText) {
    // Create an item for each post with the required n8n structure
    items.push({
      json: {
        postNumber: i + 1,
        postText: updates[i].postText
      }
    });
  }
}

// If no valid posts were processed
if (items.length === 0) {
  items.push({
    json: {
      message: "No posts found"
    }
  });
}

return items;
