const sqlite3 = require('sqlite3').verbose();
const { promisify } = require('util');

const db = new sqlite3.Database('/home/node/test.db');
const run = promisify(db.run.bind(db));

const { json } = $input.first();


let output = '';
const statement = [
  `INSERT INTO ${json.tableName}`,
  `   (${Object.keys(json.values).join(',')})`,
  `VALUES`,
  `  (${Object.keys(json.values).map(_ => '?').join(',')})`
].join(' ');
const params = Object.values(json.values);

try {
  await run(statement.trim(), params);
  output = { output: 'ok', error: null };
} catch (err) {
  output = { output: null, error: err };
} finally {
  await db.close();
}

return output;

const sqlite3 = require('sqlite3').verbose();
const { promisify } = require('util');

const db = new sqlite3.Database('/home/node/test.db');
const run = promisify(db.run.bind(db));

const { json } = $input.first();

let output = '';
const statement = [
  `UPDATE ${json.tableName}`,
  `SET`,
  `${Object.keys(json.values)
    .map(key => `${key} = ?`)
    .join(',')}`,
  `WHERE`,
  `${Object.keys(json.where)
     .map((key,idx) => `${key} = ?`)
     .join(' AND ')}`
].join(' ');
const params = [ ...Object.values(json.values), ...Object.values(json.where)];

try {
  await run(statement, params);
  output = { output: 'ok', error: null };
} catch (err) {
  output = { output: null, error: err };
} finally {
  await db.close();
}

return output;

const sqlite3 = require('sqlite3').verbose();
const { promisify } = require('util');

const db = new sqlite3.Database('/home/node/test.db');
const all = promisify(db.all.bind(db));

const { json } = $input.first();

let output = '';
const statement = [
  `SELECT * FROM ${json.tableName}`,
  json?.where && Object.keys(json?.where).length > 0
    ? `WHERE ` + Object.keys(json.where)
        .map((key,idx) => `${key} = $${idx+1}`)
        .join(' AND ')
    : ''
].join(' ');
const params = json.where ? Object.values(json.where) : undefined;

try {

  const results = await all(statement.trim(), params);

  output = { output: [].concat(results), error: null };
} catch (err) {
  output = { output: null, error: err };
} finally {
  await db.close();
}

return output

