parameters.jsCode:
const service = {
  documentation_url: $('EventRouter').first().json.data.url,
  endpoints: [],
};

const resources = Array.from(new Set($input.all().map(item => item.json.resource.toLowerCase().trim())));

for (const resource of resources) {
  const resourceLabel = resource.replace('api', '').trim();
  if (!resourceLabel) continue;
  const endpoint = {
    resource: resourceLabel[0].toUpperCase() + resourceLabel.substring(1, resourceLabel.length)
  };
  const operations = $input.all()
    .filter(item => item.json.resource.toLowerCase().trim() === resource)
    .map(item => item.json);
  endpoint.operations = operations.map(op => ({
    "operation": op.operation[0].toUpperCase() + op.operation.substring(1, op.operation.length),
    "description": op.description.match(/(^[^\.]+.)/)[0],
    "ApiUrl": op.url,
    "method": op.method.toUpperCase(),
    "method_documentation_url": op.documentation_url || ''
  }));
  service.endpoints.push(endpoint);
}

return service;

