parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "ideas": {
      "type": "array",
      "description": "An array containing exactly 1 content concept.",
      "minItems": 1,
      "maxItems": 1,
      "items": {
        "type": "object",
        "properties": {
          "concept": {
            "type": "string",
            "description": "The detailed text describing the content concept for a Single Image."
          },
          "suggested_format": {
            "type": "string",
            "description": "The post format, which MUST be 'Single Image'.",
            "const": "Single Image"
          }
        },
        "required": [
          "concept",
          "suggested_format"
        ]
      }
    }
  },
  "required": [
    "ideas"
  ]
}

{
  "type": "object",
  "properties": {
    "expanded_post_concept": {
      "type": "string",
      "description": "The elaborated visual concept, stating Single Image format and incorporating user input/platform considerations."
    },
    "prompt_options": {
      "type": "array",
      "description": "An array containing exactly TWO prompt options for the single image concept.",
      "minItems": 2,
      "maxItems": 2,
      "items": {
        "type": "object",
        "properties": {
          "option_description": {
            "type": "string",
            "description": "Briefly describes the distinct angle/style of this option (e.g., 'Option 1: Hyperrealistic...')."
          },
          "prompts": {
            "type": "array",
            "description": "Contains ONE detailed prompt string for the single image.",
            "minItems": 1,
            "maxItems": 1,
            "items": {
              "type": "string",
              "description": "A detailed image generation prompt."
            }
          }
        },
        "required": [
          "option_description",
          "prompts"
        ]
      }
    }
  },
  "required": [
    "expanded_post_concept",
    "prompt_options"
  ]
}

parameters.jsonSchemaExample:
{
	"Caption": "Thee future of call centers is here!"
}
