parameters.jsonBody:
=  {
    "trigger_id": "{{ $('Edit Fields').item.json['response']['trigger_id'] }}",
    "external_id": "TheHive Task Adder",
    "view": {
	"type": "modal",
	"callback_id": "add_task_modal",
	"title": {
		"type": "plain_text",
		"text": "Add a Task to Case",
		"emoji": true
	},
	"submit": {
		"type": "plain_text",
		"text": "Submit",
		"emoji": true
	},
	"close": {
		"type": "plain_text",
		"text": "Cancel",
		"emoji": true
	},
	"blocks": [
		{
			"type": "context",
			"block_id": "case_number_context",
			"elements": [
				{
					"type": "mrkdwn",
					"text": "{{ $('Edit Fields').item.json["response"]["message"]["blocks"][1]["text"]["text"] }}"
				},
				{
					"type": "plain_text",
					"text": "{{ $('Edit Fields').item.json.response.message.blocks[0].alt_text }}",
					"emoji": true
				}
			]
		},
		{
			"type": "input",
			"block_id": "title_block",
			"element": {
				"type": "plain_text_input",
				"action_id": "title_input",
				"placeholder": {
					"type": "plain_text",
					"text": "Enter the task title"
				}
			},
			"label": {
				"type": "plain_text",
				"text": "Title",
				"emoji": true
			}
		},
		{
			"type": "input",
			"block_id": "description_block",
			"element": {
				"type": "plain_text_input",
				"action_id": "description_input",
				"multiline": true,
				"placeholder": {
					"type": "plain_text",
					"text": "Enter a description for the task"
				}
			},
			"label": {
				"type": "plain_text",
				"text": "Description",
				"emoji": true
			}
		},
		{
			"type": "input",
			"block_id": "group_block",
			"element": {
				"type": "plain_text_input",
				"action_id": "group_input",
				"placeholder": {
					"type": "plain_text",
					"text": "Enter the group name"
				}
			},
			"label": {
				"type": "plain_text",
				"text": "Group",
				"emoji": true
			}
		},
		{
			"type": "actions",
			"block_id": "case-options",
			"elements": [
				{
					"type": "checkboxes",
					"options": [
						{
							"text": {
								"type": "mrkdwn",
								"text": ":exclamation: *Mandatory*"
							},
							"description": {
								"type": "mrkdwn",
								"text": "Make this case Mandatory"
							},
							"value": "mandatory"
						},
						{
							"text": {
								"type": "mrkdwn",
								"text": ":triangular_flag_on_post: *Flagged*"
							},
							"description": {
								"type": "mrkdwn",
								"text": "Flag this case"
							},
							"value": "flagged"
						}
					],
					"action_id": "submit-task-option"
				}
			]
		},
		{
			"type": "input",
			"block_id": "due_date_block",
			"element": {
				"type": "datepicker",
				"action_id": "due_date_input",
				"placeholder": {
					"type": "plain_text",
					"text": "Select a due date and time"
				},
				"initial_date": "{{$today.format('yyyy-MM-dd')}}"
			},
			"label": {
				"type": "plain_text",
				"text": "Due Date",
				"emoji": true
			}
		},
		{
			"type": "input",
			"block_id": "assignee_block",
			"element": {
				"type": "users_select",
				"action_id": "assignee_input",
				"placeholder": {
					"type": "plain_text",
					"text": "Select an assignee"
				}
			},
			"label": {
				"type": "plain_text",
				"text": "Assignee",
				"emoji": true
			}
		}
	]
}
}

