parameters.jsonBody:
= {
 "trigger_id": "{{ $('Parse Webhook').item.json['response']['trigger_id'] }}",
 "external_id": "Scan Report Generator",
 "view": {
	"title": {
		"type": "plain_text",
		"text": "Scan Report Generator",
		"emoji": true
	},
	"submit": {
		"type": "plain_text",
		"text": "Generate Report",
		"emoji": true
	},
	"type": "modal",
	"close": {
		"type": "plain_text",
		"text": "Cancel",
		"emoji": true
	},
	"blocks": [
		{
			"type": "image",
			"image_url": "https://upload.wikimedia.org/wikipedia/commons/thumb/2/26/Logo-Qualys.svg/300px-Logo-Qualys.svg.png",
			"alt_text": "Qualys Logo"
		},
		{
			"type": "section",
			"text": {
				"type": "mrkdwn",
				"text": "Select a template and generate a detailed scan report based on the results of your previous scans."
			}
		},
		{
			"type": "input",
			"block_id": "report_template",
			"element": {
				"type": "external_select",
				"placeholder": {
					"type": "plain_text",
					"text": "Select a report template",
					"emoji": true
				},
				"action_id": "report_template_select"
			},
			"label": {
				"type": "plain_text",
				"text": "Report Template",
				"emoji": true
			},
			"hint": {
				"type": "plain_text",
				"text": "Choose a report template from your Qualys account to structure the output."
			}
		},
		{
			"type": "input",
			"block_id": "report_title",
			"element": {
				"type": "plain_text_input",
				"action_id": "report_title_input",
				"placeholder": {
					"type": "plain_text",
					"text": "Enter a custom title for the report"
				}
			},
			"label": {
				"type": "plain_text",
				"text": "Report Title",
				"emoji": true
			},
			"hint": {
				"type": "plain_text",
				"text": "Provide a descriptive title for your report. This title will be used in the report header."
			}
		},
		{
			"type": "input",
			"block_id": "output_format",
			"element": {
				"type": "static_select",
				"placeholder": {
					"type": "plain_text",
					"text": "Select output format",
					"emoji": true
				},
				"options": [
					{
						"text": {
							"type": "plain_text",
							"text": "PDF",
							"emoji": true
						},
						"value": "pdf"
					},
					{
						"text": {
							"type": "plain_text",
							"text": "HTML",
							"emoji": true
						},
						"value": "html"
					},
					{
						"text": {
							"type": "plain_text",
							"text": "CSV",
							"emoji": true
						},
						"value": "csv"
					}
				],
				"action_id": "output_format_select"
			},
			"label": {
				"type": "plain_text",
				"text": "Output Format",
				"emoji": true
			},
			"hint": {
				"type": "plain_text",
				"text": "Choose the format in which you want the report to be generated."
			}
		}
	]
}
}

= {
 "trigger_id": "{{ $('Parse Webhook').item.json['response']['trigger_id'] }}",
 "external_id": "Scan Report Generator",
 "view": {
	"title": {
		"type": "plain_text",
		"text": "Vulnerability Scan",
		"emoji": true
	},
	"submit": {
		"type": "plain_text",
		"text": "Execute Scan",
		"emoji": true
	},
	"type": "modal",
	"close": {
		"type": "plain_text",
		"text": "Cancel",
		"emoji": true
	},
	"blocks": [
		{
			"type": "image",
			"image_url": "https://upload.wikimedia.org/wikipedia/commons/thumb/2/26/Logo-Qualys.svg/300px-Logo-Qualys.svg.png",
			"alt_text": "Qualys Logo"
		},
		{
			"type": "section",
			"text": {
				"type": "plain_text",
				"text": "Initiate a network-wide scan to detect and assess security vulnerabilities.",
				"emoji": true
			}
		},
		{
			"type": "input",
			"block_id": "option_title",
			"element": {
				"type": "plain_text_input",
				"action_id": "text_input-action",
				"initial_value": "Initial Options"
			},
			"label": {
				"type": "plain_text",
				"text": "Option Title",
				"emoji": true
			},
			"hint": {
				"type": "plain_text",
				"text": "Specify the title of the option profile to use for the scan."
			}
		},
		{
			"type": "input",
			"block_id": "scan_title",
			"element": {
				"type": "plain_text_input",
				"action_id": "text_input-action",
				"placeholder": {
					"type": "plain_text",
					"text": "Enter your scan title"
				},
				"initial_value": "n8n Scan 1"
			},
			"label": {
				"type": "plain_text",
				"text": "Scan Title",
				"emoji": true
			},
			"hint": {
				"type": "plain_text",
				"text": "Provide a descriptive title for the scan. Up to 2000 characters."
			}
		},
		{
			"type": "input",
			"block_id": "asset_groups",
			"element": {
				"type": "plain_text_input",
				"action_id": "text_input-action",
				"placeholder": {
					"type": "plain_text",
					"text": "Enter asset groups"
				},
				"initial_value": "Group1"
			},
			"label": {
				"type": "plain_text",
				"text": "Asset Groups",
				"emoji": true
			},
			"hint": {
				"type": "plain_text",
				"text": "Specify asset group titles for targeting. Multiple titles must be comma-separated."
			}
		}
	]
}
}
